var table=document.getElementById("tbl"),thead=table.tHead.cloneNode(!0);table.insertBefore(thead,table.tHead),thead.className="afix",thead.nextSibling.className="static";var update=function(e){var t=window.pageYOffset||document.documentElement.scrollTop;if(t>table.offsetTop&&t<table.offsetTop+table.offsetHeight){if("afix"!=table.className||"scroll"!=e.type){table.className="afix";for(var a=thead.rows[0].cells,l=thead.nextSibling.rows[0].cells,s=0,o=a.length;o>s;s++)a[s].style.width=l[s].offsetWidth+"px"}}else if("static"!=table.className){table.className="static";for(var a=thead.rows[0].cells,s=0,o=a.length;o>s;s++)a[s].style.width=""}};window.onscroll=window.onresize=thead.onclick=update,update();